// Helper function to get all race categories
export const getRaceCategories = () => {
  return ethnicityData.filter(item => item.parent === item.value);
};

// Helper function to get children of a parent
export const getChildrenOf = (parentCode: string) => {
  return ethnicityData.filter(item => item.parent === parentCode && item.parent !== item.value);
};

// Helper function to get all items of a specific race
export const getItemsByRace = (raceCode: string) => {
  return ethnicityData.filter(item => item.race === raceCode);
};

// Helper function to find an item by its code
export const findByCode = (code: string) => {
  return ethnicityData.find(item => item.value === code);
};

// Helper function to get the full hierarchy path for an item
export const getHierarchyPath = (code: string): string[] => {
  const item = findByCode(code);
  if (!item) return [];
  
  const path: string[] = [item.label];
  let current = item;
  
  while (current.parent !== current.value) {
    const parent = findByCode(current.parent);
    if (!parent) break;
    path.unshift(parent.label);
    current = parent;
  }
  
  return path;
};

